import React from 'react';
import { MessageCircle, Send } from 'lucide-react';

const Chatbot = ({ chatMessages, chatInput, setChatInput, handleChatSubmit }) => {
  const quickQuestions = ['Track 25-10001', 'Show statistics', 'SLA info', 'Find bottlenecks', 'Get insights'];

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleChatSubmit();
    }
  };

  const handleQuickQuestion = (question) => {
    setChatInput(question);
    // Trigger submit after a brief delay to allow state to update
    setTimeout(() => handleChatSubmit(), 100);
  };

  return (
    <div className="bg-white rounded-xl shadow-2xl overflow-hidden max-w-4xl mx-auto">
      <div className="bg-gradient-to-r from-blue-600 to-indigo-600 p-6 text-white">
        <h2 className="text-2xl font-bold flex items-center">
          <MessageCircle className="mr-3" />
          Seattle Customer Service AI Assistant
        </h2>
        <p className="text-blue-100 mt-2">Ask me anything about service requests, SLA, or insights</p>
      </div>

      <div className="h-[500px] overflow-y-auto p-6 bg-gray-50">
        {chatMessages.map((msg, idx) => (
          <div key={idx} className={`mb-4 flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}>
            <div className={`max-w-[80%] rounded-2xl px-4 py-3 ${
              msg.role === 'user' 
                ? 'bg-blue-600 text-white' 
                : 'bg-white text-gray-800 shadow-md border border-gray-200'
            }`}>
              <p className="whitespace-pre-line">{msg.content}</p>
              <p className="text-xs mt-2 opacity-70">{msg.timestamp.toLocaleTimeString()}</p>
            </div>
          </div>
        ))}
      </div>

      <div className="border-t border-gray-200 p-4 bg-white">
        <div className="flex gap-2">
          <input
            type="text"
            value={chatInput}
            onChange={(e) => setChatInput(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Type your question..."
            className="flex-1 px-4 py-3 border-2 border-gray-300 rounded-lg focus:border-blue-500 focus:ring-2 focus:ring-blue-200 outline-none"
          />
          <button
            onClick={handleChatSubmit}
            className="px-6 py-3 bg-blue-600 text-white rounded-lg font-bold hover:bg-blue-700 transition-all flex items-center justify-center"
          >
            <Send size={24} />
          </button>
        </div>
        <div className="mt-3 flex flex-wrap gap-2">
          <p className="text-xs text-gray-500 w-full mb-1">Quick questions:</p>
          {quickQuestions.map(q => (
            <button
              key={q}
              onClick={() => handleQuickQuestion(q)}
              className="px-3 py-1 bg-gray-100 text-gray-700 rounded-full text-sm hover:bg-gray-200 transition-all"
            >
              {q}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Chatbot;
