    CUSTOMER_SERVICE_REQUESTS: '5ngg-rpne.json',
    REQUEST_TRACKING: '43nw-pkdq.json'
  },
  LIMITS: {
    REQUESTS: 3000,
    TRACKING: 2000
  }
};

export const fetchSeattleData = async () => {
  try {
    console.log('ðŸ”„ Fetching data from Seattle Open Data Portal...');

    // Construct correct URLs
    const requestsUrl = `${API_CONFIG.BASE_URL}/${API_CONFIG.ENDPOINTS.CUSTOMER_SERVICE_REQUESTS}?$limit=${API_CONFIG.LIMITS.REQUESTS}&$order=created_date DESC`;
    const trackingUrl = `${API_CONFIG.BASE_URL}/${API_CONFIG.ENDPOINTS.REQUEST_TRACKING}?$limit=${API_CONFIG.LIMITS.TRACKING}&$order=updated_at DESC`;

    console.log('ðŸ“¡ Requests URL:', requestsUrl);
    console.log('ðŸ“¡ Tracking URL:', trackingUrl);

    const [requestsResponse, trackingResponse] = await Promise.all([
      fetch(requestsUrl)
        .catch(err => {
          console.error('âŒ Error fetching requests:', err);
          return null;
        }),
      fetch(trackingUrl)
        .catch(err => {
          console.error('âŒ Error fetching tracking:', err);
          return null;
        })
    ]);

    let requests = [];
    let tracking = [];

    // Process Customer Service Requests
    if (requestsResponse && requestsResponse.ok) {
      const rawRequests = await requestsResponse.json();
      console.log(`âœ… Fetched ${rawRequests.length} requests from Seattle API`);

      requests = rawRequests.map(req => ({
        service_request_number: req.service_request_number || req.servicerequestnumber || `SR-${Math.random().toString(36).substr(2, 9)}`,
        service_request_type: req.service_request_type || req.servicerequesttype || 'Unknown',
        city_department: req.city_department || req.departmentname || 'Unknown Department',
        status: req.status || req.servicerequeststatus || 'Open',
        created_date: req.created_date || req.createddate || new Date().toISOString(),
        closed_date: req.closed_date || req.closeddate || null,
        location: req.location || `${req.latitude || ''}, ${req.longitude || ''}` || 'Seattle, WA',
        council_district: req.council_district || req.councildistrict || 'Unknown',
        latitude: req.latitude || null,
        longitude: req.longitude || null,
        zip_code: req.zipcode || req.zip_code || null,
        expected_resolution_days: getExpectedResolutionDays(req.service_request_type || req.servicerequesttype),
        actual_resolution_days: null,
        sla_met: null
      }));

      // Calculate resolution times for closed requests
      requests = requests.map(req => {
        if (req.closed_date && req.created_date) {
          const created = new Date(req.created_date);
          const closed = new Date(req.closed_date);
          const days = Math.floor((closed - created) / (1000 * 60 * 60 * 24));
          return {
            ...req,
            actual_resolution_days: days,
            sla_met: days <= req.expected_resolution_days
          };
        }
        return req;
      });
    } else {
      console.warn('âš ï¸ Failed to fetch requests:', requestsResponse?.status, requestsResponse?.statusText);
    }

    // Process Request Tracking Data
    if (trackingResponse && trackingResponse.ok) {
      const rawTracking = await trackingResponse.json();
      console.log(`âœ… Fetched ${rawTracking.length} tracking records from Seattle API`);

      tracking = rawTracking.map(track => ({
        service_request_number: track.service_request_number || track.servicerequestnumber,
        status: track.status || track.statusdescription || 'In Progress',
        updated_at: track.updated_at || track.updatedat || new Date().toISOString(),
        department: track.department || track.departmentname || 'Unknown'
      }));
    } else {
      console.warn('âš ï¸ Failed to fetch tracking:', trackingResponse?.status, trackingResponse?.statusText);
    }

    // If we got real data, return it
