        requests,
        tracking,
        error: null,
        dataSource: 'Seattle Open Data Portal (Live)',
        timestamp: new Date().toISOString()
      };
    }

    // Otherwise use sample data
    throw new Error('No data returned from API');

  } catch (err) {
    console.warn('âš ï¸ Using sample data:', err.message);
    const sampleData = generateEnhancedSampleData();
    return {
      ...sampleData,
      error: `API unavailable: ${err.message}`,
      dataSource: 'Sample Data (Demo)',
      timestamp: new Date().toISOString()
    };
  }
};

// Helper function to get expected resolution days based on request type
const getExpectedResolutionDays = (requestType) => {
  const slaMap = {
    'Abandoned Vehicle': 3,
    'Graffiti': 7,
    'Pothole': 3,
    'Parking Enforcement': 1,
    'Unauthorized Encampment': 5,
    'Street Light Out': 5,
    'Illegal Dumping': 7,
    'Tree Maintenance': 14,
    'Traffic Signal': 2,
    'Street Sign': 5,
    'Park Maintenance': 10,
    'Sidewalk Repair': 14,
    'Water Main Break': 1,
    'Sewer Issue': 2
  };

  if (!requestType) return 5;

  // Try exact match
  if (slaMap[requestType]) return slaMap[requestType];

  // Try partial match
  for (const [key, value] of Object.entries(slaMap)) {
    if (requestType.toLowerCase().includes(key.toLowerCase())) {
      return value;
    }
  }

  return 5; // Default
};

// Enhanced sample data generator
const generateEnhancedSampleData = () => {
  const departments = [
    'Seattle Police Department',
    'Seattle Department of Transportation',
    'Seattle Public Utilities',
    'Parks and Recreation',
    'Human Services Department',
    'Seattle City Light',
    'Seattle Fire Department'
  ];

  const requestTypes = [
    { type: 'Abandoned Vehicle', avgDays: 3, targetDays: 3 },
    { type: 'Graffiti', avgDays: 5, targetDays: 7 },
    { type: 'Pothole', avgDays: 2, targetDays: 3 },
    { type: 'Parking Enforcement', avgDays: 1, targetDays: 1 },
    { type: 'Unauthorized Encampment', avgDays: 8, targetDays: 5 },
    { type: 'Street Light Out', avgDays: 4, targetDays: 5 },
    { type: 'Illegal Dumping', avgDays: 6, targetDays: 7 },
    { type: 'Tree Maintenance', avgDays: 12, targetDays: 14 },
    { type: 'Traffic Signal Malfunction', avgDays: 1, targetDays: 2 },
    { type: 'Sidewalk Repair', avgDays: 15, targetDays: 14 },
    { type: 'Water Main Break', avgDays: 1, targetDays: 1 },
    { type: 'Park Maintenance', avgDays: 8, targetDays: 10 }
  ];

  const seattleLocations = [
    'Capitol Hill, Seattle, WA',
    'Ballard, Seattle, WA',
    'Fremont, Seattle, WA',
    'Queen Anne, Seattle, WA',
    'University District, Seattle, WA',
    'Greenwood, Seattle, WA',
    'Wallingford, Seattle, WA',
    'Ravenna, Seattle, WA',
    'Green Lake, Seattle, WA',
    'Northgate, Seattle, WA'
  ];

  const requests = [];
  const tracking = [];

  for (let i = 0; i < 300; i++) {
    const requestType = requestTypes[Math.floor(Math.random() * requestTypes.length)];
    const department = departments[Math.floor(Math.random() * departments.length)];
    const createdDate = new Date(2025, 10, 6 - Math.floor(Math.random() * 120));
    const daysOpen = Math.floor(Math.random() * 35);
    const willClose = Math.random() > 0.35;
    const closedDate = willClose ? new Date(createdDate.getTime() + daysOpen * 24 * 60 * 60 * 1000) : null;
    const status = closedDate ? 'Closed' : (Math.random() > 0.4 ? 'In Progress' : 'Open');

    const request = {
      service_request_number: `25-${String(10000 + i).padStart(6, '0')}`,
      service_request_type: requestType.type,
      city_department: department,
      status: status,
      created_date: createdDate.toISOString(),
      closed_date: closedDate?.toISOString(),
      days_to_resolve: closedDate ? daysOpen : null,
      expected_resolution_days: requestType.targetDays,
      actual_resolution_days: closedDate ? daysOpen : null,
      sla_met: closedDate ? (daysOpen <= requestType.targetDays) : null,
      location: seattleLocations[Math.floor(Math.random() * seattleLocations.length)],
      council_district: `District ${Math.floor(Math.random() * 7 + 1)}`,
      latitude: 47.6062 + (Math.random() - 0.5) * 0.1,
      longitude: -122.3321 + (Math.random() - 0.5) * 0.1,
      zip_code: `981${Math.floor(Math.random() * 30 + 1).toString().padStart(2, '0')}`
    };

    requests.push(request);

    const statusUpdates = [
      { status: 'Request Received', days: 0 },
      { status: 'Routed to Department', days: 0.5 },
      { status: 'Assigned to Staff', days: 1 },
      { status: 'Assessment Complete', days: 2 },
      { status: 'Work Scheduled', days: 3 },
      { status: 'Work in Progress', days: 5 },
      { status: 'Work Complete', days: daysOpen - 1 },
      { status: 'Verification', days: daysOpen - 0.5 }
    ];

    const numUpdates = status === 'Closed' ? statusUpdates.length : Math.min(Math.floor(Math.random() * 5 + 2), statusUpdates.length - 2);

    for (let j = 0; j < numUpdates; j++) {
      const update = statusUpdates[j];
      tracking.push({
        service_request_number: request.service_request_number,
        status: update.status,
        updated_at: new Date(createdDate.getTime() + update.days * 24 * 60 * 60 * 1000).toISOString(),
        department: department
      });
    }
  }

  return { requests, tracking };
};

