import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Dashboard from './components/Dashboard';
import StatusTracker from './components/StatusTracker';
import Chatbot from './components/Chatbot';
import VoiceMode from './components/VoiceMode';
import DeepInsights from './components/DeepInsights';
import { fetchSeattleData } from './services/dataService';
import { generateDeepInsights } from './services/insightsEngine';
import { generateChatbotResponse, initializeChatbot } from './services/chatbotEngine';
import { processMetrics, calculateBacklog } from './utils/calculations';
import { AlertCircle, Database, Clock } from 'lucide-react';

function App() {
  const [loading, setLoading] = useState(true);
  const [requestData, setRequestData] = useState([]);
  const [trackingData, setTrackingData] = useState([]);
  const [error, setError] = useState(null);
  const [dataSource, setDataSource] = useState('');
  const [timestamp, setTimestamp] = useState('');
  const [slaMetrics, setSlaMetrics] = useState({});
  const [backlogData, setBacklogData] = useState([]);
  const [activeView, setActiveView] = useState('dashboard');
  const [chatMessages, setChatMessages] = useState([]);
  const [chatInput, setChatInput] = useState('');
  const [voiceMode, setVoiceMode] = useState(false);
  const [insights, setInsights] = useState([]);
  const [trackerSearch, setTrackerSearch] = useState('');
  const [selectedRequest, setSelectedRequest] = useState(null);

  useEffect(() => {
    loadData();
    setChatMessages(initializeChatbot());
  }, []);

  const loadData = async () => {
    setLoading(true);
    const { requests, tracking, error, dataSource, timestamp } = await fetchSeattleData();

    setRequestData(requests);
    setTrackingData(tracking);
    setError(error);
    setDataSource(dataSource);
    setTimestamp(timestamp);

    const metrics = processMetrics(requests);
    setSlaMetrics(metrics);

    const backlog = calculateBacklog(requests);
    setBacklogData(backlog);

    const deepInsights = generateDeepInsights(requests, tracking, metrics);
    setInsights(deepInsights);

    setLoading(false);
  };

  const handleChatSubmit = () => {
    if (!chatInput.trim()) return;

    const userMessage = {
      role: 'user',
      content: chatInput,
      timestamp: new Date()
    };

    setChatMessages(prev => [...prev, userMessage]);

    setTimeout(() => {
      const response = generateChatbotResponse(chatInput, requestData, trackingData, slaMetrics, insights);
      setChatMessages(prev => [...prev, {
        role: 'assistant',
        content: response,
        timestamp: new Date()
      }]);
    }, 500);

    setChatInput('');
  };

  const handleVoiceMode = () => {
    setVoiceMode(!voiceMode);
    if (!voiceMode) {
      setChatMessages(prev => [...prev, {
        role: 'assistant',
        content: 'üé§ Voice mode activated. I\'m listening... (Demo mode - please type your question)',
        timestamp: new Date()
      }]);
    }
  };

  const slaChartData = Object.entries(slaMetrics.byType || {}).slice(0, 8).map(([type, data]) => ({
    name: type.length > 20 ? type.substring(0, 20) + '...' : type,
    'Expected Days': parseFloat(data.expectedDays),
    'Actual Avg Days': parseFloat(data.avgResolutionDays),
    'SLA Rate %': parseFloat(data.slaComplianceRate)
  }));

  const backlogScatterData = backlogData.slice(0, 50).map(item => ({
    daysOpen: item.daysOpen,
    expected: item.expectedDays,
    name: item.service_request_type
  }));

  const stats = {
    total: requestData.length,
    open: requestData.filter(r => r.status === 'Open').length,
    closed: requestData.filter(r => r.status === 'Closed').length,
    backlogCount: backlogData.length,
    pastDueCount: backlogData.filter(b => b.pastDue).length
  };

  const trackerRequests = requestData.filter(r => 
    r.service_request_number.toLowerCase().includes(trackerSearch.toLowerCase()) ||
    r.service_request_type.toLowerCase().includes(trackerSearch.toLowerCase())
  ).slice(0, 20);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
        <div className="text-center">
          <div className="animate-spin rounded-full h-20 w-20 border-b-4 border-blue-600 mx-auto"></div>
          <p className="mt-6 text-xl text-gray-700 font-semibold">Loading Seattle Customer Service Data...</p>
          <p className="mt-2 text-gray-500">Connecting to Open Data Portal...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-4 md:p-6">
      <div className="max-w-7xl mx-auto">
        <Header activeView={activeView} setActiveView={setActiveView} />

        {/* Data Source Banner */}
        <div className={`rounded-lg shadow-lg p-4 mb-6 ${error ? 'bg-yellow-50 border-l-4 border-yellow-400' : 'bg-green-50 border-l-4 border-green-400'}`}>
          <div className="flex items-start justify-between">
            <div className="flex items-start">
              {error ? (
                <AlertCircle className="text-yellow-600 mr-3 mt-1" size={24} />
              ) : (
                <Database className="text-green-600 mr-3 mt-1" size={24} />
              )}
              <div>
                <p className={`font-bold text-lg ${error ? 'text-yellow-800' : 'text-green-800'}`}>
                  {dataSource}
                </p>
                <p className="text-sm text-gray-600 mt-1">
                  {error ? (
                    <>Demo Mode: Using sample data for demonstration. {error}</>
                  ) : (
                    <>Connected to Seattle Open Data Portal - Live data from city databases</>
                  )}
                </p>
                <div className="flex items-center mt-2 text-xs text-gray-500">
                  <Clock size={14} className="mr-1" />
                  Last updated: {new Date(timestamp).toLocaleString()}
                </div>
              </div>
            </div>
            {!error && (
              <button
                onClick={loadData}
                className="px-4 py-2 bg-green-600 text-white rounded-lg text-sm font-semibold hover:bg-green-700 transition-all"
              >
                Refresh Data
              </button>
            )}
          </div>
        </div>

        {/* Main Content */}
        {activeView === 'dashboard' && (
          <Dashboard stats={stats} slaChartData={slaChartData} backlogScatterData={backlogScatterData} />
        )}

        {activeView === 'tracker' && (
          <StatusTracker 
            trackerSearch={trackerSearch}
            setTrackerSearch={setTrackerSearch}
            trackerRequests={trackerRequests}
            trackingData={trackingData}
            setSelectedRequest={setSelectedRequest}
          />
        )}

        {activeView === 'chatbot' && (
          <Chatbot 
            chatMessages={chatMessages}
            chatInput={chatInput}
            setChatInput={setChatInput}
            handleChatSubmit={handleChatSubmit}
          />
        )}

        {activeView === 'voice' && (
          <VoiceMode 
            chatMessages={chatMessages}
