// src/components/Dashboard.jsx
// Main dashboard with statistics and visualizations

import React, { useState, useEffect } from 'react';
import { BarChart, Bar, LineChart, Line, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { TrendingUp, AlertCircle, Clock, CheckCircle, XCircle, Activity } from 'lucide-react';

const Dashboard = ({ data }) => {
  const [stats, setStats] = useState({
    total: 0,
    open: 0,
    inProgress: 0,
    closed: 0,
    avgResolutionTime: 0,
    slaCompliance: 0
  });
  const [chartData, setChartData] = useState({
    statusData: [],
    departmentData: [],
    trendData: []
  });

  // Safely access data
  const requests = data?.requests || [];
  const tracking = data?.tracking || [];

  useEffect(() => {
    if (requests.length > 0) {
      calculateStats();
      prepareChartData();
    }
  }, [requests]);

  const calculateStats = () => {
    const total = requests.length;
    const open = requests.filter(r => r.status === 'Open').length;
    const inProgress = requests.filter(r => r.status === 'In Progress').length;
    const closed = requests.filter(r => r.status === 'Closed').length;

    // Calculate average resolution time
    const closedWithTime = requests.filter(r => r.actual_resolution_days !== null);
    const avgResolutionTime = closedWithTime.length > 0
      ? Math.round(closedWithTime.reduce((sum, r) => sum + r.actual_resolution_days, 0) / closedWithTime.length)
      : 0;

    // Calculate SLA compliance
    const withSLA = requests.filter(r => r.sla_met !== null);
    const slaCompliance = withSLA.length > 0
      ? Math.round((withSLA.filter(r => r.sla_met).length / withSLA.length) * 100)
      : 0;

    setStats({
      total,
      open,
      inProgress,
      closed,
      avgResolutionTime,
      slaCompliance
    });
  };

  const prepareChartData = () => {
    // Status distribution
    const statusData = [
      { name: 'Open', value: requests.filter(r => r.status === 'Open').length, color: '#ef4444' },
      { name: 'In Progress', value: requests.filter(r => r.status === 'In Progress').length, color: '#3b82f6' },
      { name: 'Closed', value: requests.filter(r => r.status === 'Closed').length, color: '#10b981' }
    ];

    // Department distribution (top 10)
    const deptCount = {};
    requests.forEach(r => {
      const dept = r.city_department || 'Unknown';
      deptCount[dept] = (deptCount[dept] || 0) + 1;
    });
    const departmentData = Object.entries(deptCount)
      .sort((a, b) => b[1] - a[1])
      .slice(0, 10)
      .map(([name, count]) => ({ name: name.replace(/^[A-Z]+-/, ''), count }));

    // Monthly trend
    const monthCount = {};
    requests.forEach(r => {
      try {
        const date = new Date(r.created_date);
        const month = date.toLocaleString('default', { month: 'short', year: 'numeric' });
        monthCount[month] = (monthCount[month] || 0) + 1;
      } catch (e) {}
    });
    const trendData = Object.entries(monthCount)
      .map(([month, count]) => ({ month, count }))
      .slice(-6); // Last 6 months

    setChartData({
      statusData,
      departmentData,
      trendData
    });
  };

  if (!requests || requests.length === 0) {
    return (
      <div className="flex items-center justify-center h-96">
        <div className="text-center">
          <div className="text-6xl mb-4">üìä</div>
          <h2 className="text-2xl font-bold text-gray-800 mb-2">No Data Available</h2>
          <p className="text-gray-600">Please load data to see the dashboard</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-3xl font-bold text-gray-800 mb-2">Seattle Customer Service Dashboard</h2>
        <p className="text-gray-600">Overview of service request data for 2025</p>
        <div className="mt-4 inline-block bg-blue-100 text-blue-800 px-4 py-2 rounded-full text-sm font-medium">
          {data?.dataSource || 'Live Data'}
        </div>
      </div>

      {/* KPI Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {/* Total Requests */}
        <div className="bg-white rounded-lg shadow-md p-6 border-l-4 border-blue-600">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-500 text-sm font-medium mb-1">Total Requests</p>
              <p className="text-3xl font-bold text-gray-800">{stats.total.toLocaleString()}</p>
            </div>
            <Activity className="w-12 h-12 text-blue-600 opacity-75" />
          </div>
        </div>

        {/* Open Requests */}
        <div className="bg-white rounded-lg shadow-md p-6 border-l-4 border-red-600">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-500 text-sm font-medium mb-1">Open Requests</p>
              <p className="text-3xl font-bold text-gray-800">{stats.open.toLocaleString()}</p>
              <p className="text-xs text-gray-500 mt-1">
                {stats.total > 0 ? Math.round((stats.open / stats.total) * 100) : 0}% of total
              </p>
            </div>
            <AlertCircle className="w-12 h-12 text-red-600 opacity-75" />
          </div>
        </div>

        {/* In Progress */}
        <div className="bg-white rounded-lg shadow-md p-6 border-l-4 border-yellow-600">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-500 text-sm font-medium mb-1">In Progress</p>
              <p className="text-3xl font-bold text-gray-800">{stats.inProgress.toLocaleString()}</p>
              <p className="text-xs text-gray-500 mt-1">
                {stats.total > 0 ? Math.round((stats.inProgress / stats.total) * 100) : 0}% of total
              </p>
            </div>
            <Clock className="w-12 h-12 text-yellow-600 opacity-75" />
          </div>
        </div>

        {/* Closed Requests */}
        <div className="bg-white rounded-lg shadow-md p-6 border-l-4 border-green-600">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-500 text-sm font-medium mb-1">Closed Requests</p>
              <p className="text-3xl font-bold text-gray-800">{stats.closed.toLocaleString()}</p>
              <p className="text-xs text-gray-500 mt-1">
                {stats.total > 0 ? Math.round((stats.closed / stats.total) * 100) : 0}% of total
              </p>
            </div>
            <CheckCircle className="w-12 h-12 text-green-600 opacity-75" />
          </div>
        </div>
      </div>

      {/* Performance Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Average Resolution Time */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h3 className="text-xl font-bold text-gray-800 mb-4 flex items-center gap-2">
            <Clock className="text-blue-600" />
            Average Resolution Time
          </h3>
          <div className="text-center py-8">
            <div className="text-6xl font-bold text-blue-600 mb-2">
              {stats.avgResolutionTime}
            </div>
            <div className="text-xl text-gray-600">days</div>
          </div>
        </div>

        {/* SLA Compliance */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h3 className="text-xl font-bold text-gray-800 mb-4 flex items-center gap-2">
            <TrendingUp className="text-green-600" />
            SLA Compliance Rate
          </h3>
          <div className="text-center py-8">
            <div className="text-6xl font-bold text-green-600 mb-2">
              {stats.slaCompliance}%
            </div>
            <div className="text-sm text-gray-600 mt-4">
              {stats.slaCompliance >= 80 ? '‚úÖ Excellent' : stats.slaCompliance >= 60 ? '‚ö†Ô∏è Good' : '‚ùå Needs Improvement'}
            </div>
          </div>
        </div>
      </div>

      {/* Charts Section */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Status Distribution Pie Chart */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h3 className="text-xl font-bold text-gray-800 mb-4">Status Distribution</h3>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={chartData.statusData}
                cx="50%"
                cy="50%"
                labelLine={false}
                label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
                outerRadius={80}
                fill="#8884d8"
                dataKey="value"
              >
                {chartData.statusData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={entry.color} />
                ))}
              </Pie>
              <Tooltip />
            </PieChart>
          </ResponsiveContainer>
        </div>

        {/* Monthly Trend */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h3 className="text-xl font-bold text-gray-800 mb-4">Request Trends (Last 6 Months)</h3>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={chartData.trendData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="month" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="count" stroke="#3b82f6" strokeWidth={2} />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Top Departments Bar Chart */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h3 className="text-xl font-bold text-gray-800 mb-4">Top 10 Departments by Request Volume</h3>
        <ResponsiveContainer width="100%" height={400}>
          <BarChart data={chartData.departmentData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" angle={-45} textAnchor="end" height={120} />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="count" fill="#3b82f6" />
          </BarChart>
        </ResponsiveContainer>
      </div>

      {/* Recent Requests Table */}
      <div className="bg-white rounded-lg shadow-md overflow-hidden">
        <div className="p-6 border-b">
          <h3 className="text-xl font-bold text-gray-800">Recent Service Requests</h3>
        </div>
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Request #</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Type</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Department</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Status</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Created</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {requests.slice(0, 10).map((request, index) => (
                <tr key={index} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-blue-600">
                    {request.service_request_number}
                  </td>
                  <td className="px-6 py-4 text-sm text-gray-900">
                    {request.service_request_type}
                  </td>
                  <td className="px-6 py-4 text-sm text-gray-600">
                    {request.city_department}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 py-1 text-xs font-semibold rounded-full ${
                      request.status === 'Closed' ? 'bg-green-100 text-green-800' :
                      request.status === 'In Progress' ? 'bg-blue-100 text-blue-800' :
                      'bg-red-100 text-red-800'
                    }`}>
                      {request.status}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {new Date(request.created_date).toLocaleDateString()}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
