// Fixed Dashboard with proper data handling

import React, { useState, useEffect } from 'react';
import { BarChart, Bar, LineChart, Line, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { TrendingUp, AlertCircle, Clock, CheckCircle, Activity } from 'lucide-react';

const Dashboard = ({ data }) => {
  const [stats, setStats] = useState({
    total: 0,
    open: 0,
    inProgress: 0,
    closed: 0
  });
  const [statusData, setStatusData] = useState([]);
  const [typeData, setTypeData] = useState([]);
  const [departmentData, setDepartmentData] = useState([]);
  const [slaData, setSlaData] = useState([]);

  useEffect(() => {
    if (data?.requests && Array.isArray(data.requests)) {
      processData(data.requests);
    }
  }, [data]);

  const processData = (requests) => {
    // Calculate statistics
    const total = requests.length;
    const open = requests.filter(r => r.status === 'Open').length;
    const inProgress = requests.filter(r => r.status === 'In Progress').length;
    const closed = requests.filter(r => r.status === 'Closed').length;

    setStats({ total, open, inProgress, closed });

    // Status breakdown for pie chart
    setStatusData([
      { name: 'Open', value: open, color: '#ef4444' },
      { name: 'In Progress', value: inProgress, color: '#3b82f6' },
      { name: 'Closed', value: closed, color: '#10b981' }
    ]);

    // Top request types
    const typeCounts = {};
    requests.forEach(r => {
      typeCounts[r.service_request_type] = (typeCounts[r.service_request_type] || 0) + 1;
    });
    const topTypes = Object.entries(typeCounts)
      .sort((a, b) => b[1] - a[1])
      .slice(0, 10)
      .map(([name, count]) => ({ name, count }));
    setTypeData(topTypes);

    // Department breakdown
    const deptCounts = {};
    requests.forEach(r => {
      const dept = r.city_department || 'Unknown';
      deptCounts[dept] = (deptCounts[dept] || 0) + 1;
    });
    const topDepts = Object.entries(deptCounts)
      .sort((a, b) => b[1] - a[1])
      .slice(0, 8)
      .map(([name, count]) => ({ name: name.replace('Seattle', '').trim(), count }));
    setDepartmentData(topDepts);

    // SLA Performance
    const closedRequests = requests.filter(r => r.status === 'Closed' && r.sla_met !== null);
    const slaMet = closedRequests.filter(r => r.sla_met === true).length;
    const slaMissed = closedRequests.filter(r => r.sla_met === false).length;
    setSlaData([
      { name: 'SLA Met', value: slaMet, color: '#10b981' },
      { name: 'SLA Missed', value: slaMissed, color: '#ef4444' }
    ]);
  };

  const COLORS = ['#ef4444', '#3b82f6', '#10b981'];

  return (
    <div className="space-y-6">
      {/* Data Source Banner */}
      {data?.dataSource && (
        <div className="bg-gradient-to-r from-green-50 to-blue-50 border-l-4 border-green-600 p-4 rounded-lg">
          <div className="flex items-center gap-2">
            <CheckCircle className="text-green-600 w-5 h-5" />
            <span className="font-semibold text-gray-800">Live Data: {data.dataSource}</span>
          </div>
        </div>
      )}

      {/* KPI Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <div className="bg-white rounded-lg shadow-lg p-6 border-l-4 border-blue-600">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-500 text-sm">Total Requests</p>
              <p className="text-3xl font-bold text-gray-800">{stats.total.toLocaleString()}</p>
            </div>
            <Activity className="text-blue-600 w-12 h-12" />
          </div>
        </div>

        <div className="bg-white rounded-lg shadow-lg p-6 border-l-4 border-red-600">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-500 text-sm">Open</p>
              <p className="text-3xl font-bold text-gray-800">{stats.open.toLocaleString()}</p>
            </div>
            <AlertCircle className="text-red-600 w-12 h-12" />
          </div>
        </div>

        <div className="bg-white rounded-lg shadow-lg p-6 border-l-4 border-blue-600">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-500 text-sm">In Progress</p>
              <p className="text-3xl font-bold text-gray-800">{stats.inProgress.toLocaleString()}</p>
            </div>
            <Clock className="text-blue-600 w-12 h-12" />
          </div>
        </div>

        <div className="bg-white rounded-lg shadow-lg p-6 border-l-4 border-green-600">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-500 text-sm">Closed</p>
              <p className="text-3xl font-bold text-gray-800">{stats.closed.toLocaleString()}</p>
            </div>
            <CheckCircle className="text-green-600 w-12 h-12" />
          </div>
        </div>
      </div>

      {/* Charts Row 1 */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Status Distribution */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-xl font-bold text-gray-800 mb-4">Status Distribution</h3>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={statusData}
                cx="50%"
                cy="50%"
                labelLine={false}
                label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                outerRadius={100}
                fill="#8884d8"
                dataKey="value"
              >
                {statusData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={entry.color} />
                ))}
              </Pie>
              <Tooltip />
            </PieChart>
          </ResponsiveContainer>
        </div>

        {/* SLA Performance */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-xl font-bold text-gray-800 mb-4">SLA Performance</h3>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={slaData}
                cx="50%"
                cy="50%"
                labelLine={false}
                label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                outerRadius={100}
                fill="#8884d8"
                dataKey="value"
              >
                {slaData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={entry.color} />
                ))}
              </Pie>
              <Tooltip />
            </PieChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Charts Row 2 */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Top Request Types */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-xl font-bold text-gray-800 mb-4">Top Request Types</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={typeData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" angle={-45} textAnchor="end" height={100} />
              <YAxis />
              <Tooltip />
              <Bar dataKey="count" fill="#3b82f6" />
            </BarChart>
          </ResponsiveContainer>
        </div>

        {/* Department Workload */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-xl font-bold text-gray-800 mb-4">Department Workload</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={departmentData} layout="vertical">
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis type="number" />
              <YAxis dataKey="name" type="category" width={150} />
              <Tooltip />
              <Bar dataKey="count" fill="#8b5cf6" />
            </BarChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Recent Requests Table */}
      <div className="bg-white rounded-lg shadow-lg p-6">
        <h3 className="text-xl font-bold text-gray-800 mb-4">Recent Requests</h3>
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Request #</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Type</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Department</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Status</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Created</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {data?.requests?.slice(0, 10).map((req, idx) => (
                <tr key={idx} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-blue-600">
                    {req.service_request_number}
                  </td>
                  <td className="px-6 py-4 text-sm text-gray-900">{req.service_request_type}</td>
                  <td className="px-6 py-4 text-sm text-gray-600">{req.city_department}</td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 py-1 text-xs font-semibold rounded-full ${
                      req.status === 'Open' ? 'bg-red-100 text-red-800' :
                      req.status === 'In Progress' ? 'bg-blue-100 text-blue-800' :
                      'bg-green-100 text-green-800'
                    }`}>
                      {req.status}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {new Date(req.created_date).toLocaleDateString()}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
