// src/services/dataService.js

const API_CONFIG = {
  BASE_URL: 'https://data.seattle.gov/resource',
  ENDPOINTS: {
    CUSTOMER_SERVICE_REQUESTS: '5ngg-rpne.json',
    REQUEST_TRACKING: '43nw-pkdq.json'
  },
  LIMITS: {
    REQUESTS: 1000,  // Reduced for faster loading
    TRACKING: 500    // Reduced for faster loading
  }
};

export const fetchSeattleData = async () => {
  try {
    console.log('ðŸ”„ Fetching data from Seattle Open Data Portal...');

    // Construct URLs without $order parameter (causing 400 error)
    const requestsUrl = `${API_CONFIG.BASE_URL}/${API_CONFIG.ENDPOINTS.CUSTOMER_SERVICE_REQUESTS}?$limit=${API_CONFIG.LIMITS.REQUESTS}`;
    const trackingUrl = `${API_CONFIG.BASE_URL}/${API_CONFIG.ENDPOINTS.REQUEST_TRACKING}?$limit=${API_CONFIG.LIMITS.TRACKING}`;

    console.log('ðŸ“¡ Fetching requests from:', requestsUrl);
    console.log('ðŸ“¡ Fetching tracking from:', trackingUrl);

    const [requestsResponse, trackingResponse] = await Promise.all([
      fetch(requestsUrl, {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
        }
      }).catch(err => {
        console.error('âŒ Error fetching requests:', err);
        return null;
      }),
      fetch(trackingUrl, {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
        }
      }).catch(err => {
        console.error('âŒ Error fetching tracking:', err);
        return null;
      })
    ]);

    let requests = [];
    let tracking = [];

    // Process Customer Service Requests
    if (requestsResponse && requestsResponse.ok) {
      const rawRequests = await requestsResponse.json();
      console.log(`âœ… SUCCESS! Fetched ${rawRequests.length} requests from Seattle API`);

      requests = rawRequests.map(req => ({
        service_request_number: req.service_request_number || req.servicerequestnumber || `SR-${Math.random().toString(36).substr(2, 9)}`,
        service_request_type: req.service_request_type || req.servicerequesttype || 'Unknown',
        city_department: req.city_department || req.departmentname || 'Unknown Department',
        status: req.status || req.servicerequeststatus || 'Open',
        created_date: req.created_date || req.createddate || new Date().toISOString(),
        closed_date: req.closed_date || req.closeddate || null,
        location: req.location || 'Seattle, WA',
        council_district: req.council_district || req.councildistrict || 'Unknown',
        latitude: req.latitude || null,
        longitude: req.longitude || null,
        zip_code: req.zipcode || req.zip_code || null,
        expected_resolution_days: getExpectedResolutionDays(req.service_request_type || req.servicerequesttype),
        actual_resolution_days: null,
        sla_met: null
      }));

      // Calculate resolution times for closed requests
      requests = requests.map(req => {
        if (req.closed_date && req.created_date) {
          const created = new Date(req.created_date);
          const closed = new Date(req.closed_date);
          const days = Math.floor((closed - created) / (1000 * 60 * 60 * 24));
          return {
            ...req,
            actual_resolution_days: days,
            sla_met: days <= req.expected_resolution_days
          };
        }
        return req;
      });

      // Sort by created_date (most recent first) in JavaScript
      requests.sort((a, b) => new Date(b.created_date) - new Date(a.created_date));

    } else {
      const errorText = requestsResponse ? await requestsResponse.text() : 'No response';
      console.error('âŒ Failed to fetch requests:', requestsResponse?.status, errorText);
    }

    // Process Request Tracking Data
    if (trackingResponse && trackingResponse.ok) {
      const rawTracking = await trackingResponse.json();
      console.log(`âœ… SUCCESS! Fetched ${rawTracking.length} tracking records from Seattle API`);

      tracking = rawTracking.map(track => ({
        service_request_number: track.service_request_number || track.servicerequestnumber,
        status: track.status || track.statusdescription || 'In Progress',
        updated_at: track.updated_at || track.updatedat || new Date().toISOString(),
        department: track.department || track.departmentname || 'Unknown'
      }));

      // Sort by updated_at (most recent first) in JavaScript
      tracking.sort((a, b) => new Date(b.updated_at) - new Date(a.updated_at));

    } else {
      const errorText = trackingResponse ? await trackingResponse.text() : 'No response';
      console.error('âŒ Failed to fetch tracking:', trackingResponse?.status, errorText);
    }

    // If we got real data, return it
    if (requests.length > 0) {
      console.log('âœ… Returning LIVE data from Seattle Open Data Portal');
      return {
        requests,
        tracking,
        error: null,
        dataSource: 'Seattle Open Data Portal (Live)',
        timestamp: new Date().toISOString()
      };
    }

    // Otherwise use sample data
    throw new Error('No data returned from API');

  } catch (err) {
    console.warn('âš ï¸ Using sample data:', err.message);
    const sampleData = generateEnhancedSampleData();
    return {
      ...sampleData,
      error: `API unavailable: ${err.message}`,
      dataSource: 'Sample Data (Demo)',
      timestamp: new Date().toISOString()
    };
  }
};

// Helper function to get expected resolution days based on request type
const getExpectedResolutionDays = (requestType) => {
  const slaMap = {
    'Abandoned Vehicle': 3,
    'Graffiti': 7,
    'Pothole': 3,
    'Parking Enforcement': 1,
    'Unauthorized Encampment': 5,
    'Street Light Out': 5,
    'Illegal Dumping': 7,
    'Tree Maintenance': 14,
    'Traffic Signal': 2,
    'Street Sign': 5,
    'Park Maintenance': 10,
    'Sidewalk Repair': 14,
    'Water Main Break': 1,
    'Sewer Issue': 2
  };

  if (!requestType) return 5;

  // Try exact match
  if (slaMap[requestType]) return slaMap[requestType];

  // Try partial match
  for (const [key, value] of Object.entries(slaMap)) {
    if (requestType.toLowerCase().includes(key.toLowerCase())) {
      return value;
    }
  }

  return 5; // Default
};

// Enhanced sample data generator
const generateEnhancedSampleData = () => {
  const departments = [
    'Seattle Police Department',
    'Seattle Department of Transportation',
    'Seattle Public Utilities',
    'Parks and Recreation',
    'Human Services Department',
    'Seattle City Light',
    'Seattle Fire Department'
  ];

  const requestTypes = [
    { type: 'Abandoned Vehicle', avgDays: 3, targetDays: 3 },
    { type: 'Graffiti', avgDays: 5, targetDays: 7 },
    { type: 'Pothole', avgDays: 2, targetDays: 3 },
    { type: 'Parking Enforcement', avgDays: 1, targetDays: 1 },
    { type: 'Unauthorized Encampment', avgDays: 8, targetDays: 5 },
    { type: 'Street Light Out', avgDays: 4, targetDays: 5 },
    { type: 'Illegal Dumping', avgDays: 6, targetDays: 7 },
    { type: 'Tree Maintenance', avgDays: 12, targetDays: 14 },
    { type: 'Traffic Signal Malfunction', avgDays: 1, targetDays: 2 },
    { type: 'Sidewalk Repair', avgDays: 15, targetDays: 14 },
    { type: 'Water Main Break', avgDays: 1, targetDays: 1 },
    { type: 'Park Maintenance', avgDays: 8, targetDays: 10 }
  ];

  const seattleLocations = [
    'Capitol Hill, Seattle, WA',
    'Ballard, Seattle, WA',
    'Fremont, Seattle, WA',
    'Queen Anne, Seattle, WA',
    'University District, Seattle, WA',
    'Greenwood, Seattle, WA',
    'Wallingford, Seattle, WA',
    'Ravenna, Seattle, WA',
    'Green Lake, Seattle, WA',
    'Northgate, Seattle, WA'
  ];

  const requests = [];
  const tracking = [];

  for (let i = 0; i < 300; i++) {
    const requestType = requestTypes[Math.floor(Math.random() * requestTypes.length)];
    const department = departments[Math.floor(Math.random() * departments.length)];
    const createdDate = new Date(2025, 10, 6 - Math.floor(Math.random() * 120));
    const daysOpen = Math.floor(Math.random() * 35);
    const willClose = Math.random() > 0.35;
    const closedDate = willClose ? new Date(createdDate.getTime() + daysOpen * 24 * 60 * 60 * 1000) : null;
    const status = closedDate ? 'Closed' : (Math.random() > 0.4 ? 'In Progress' : 'Open');

    const request = {
      service_request_number: `25-${String(10000 + i).padStart(6, '0')}`,
      service_request_type: requestType.type,
      city_department: department,
      status: status,
      created_date: createdDate.toISOString(),
      closed_date: closedDate?.toISOString(),
      days_to_resolve: closedDate ? daysOpen : null,
      expected_resolution_days: requestType.targetDays,
      actual_resolution_days: closedDate ? daysOpen : null,
      sla_met: closedDate ? (daysOpen <= requestType.targetDays) : null,
      location: seattleLocations[Math.floor(Math.random() * seattleLocations.length)],
      council_district: `District ${Math.floor(Math.random() * 7 + 1)}`,
      latitude: 47.6062 + (Math.random() - 0.5) * 0.1,
      longitude: -122.3321 + (Math.random() - 0.5) * 0.1,
      zip_code: `981${Math.floor(Math.random() * 30 + 1).toString().padStart(2, '0')}`
    };

    requests.push(request);

    const statusUpdates = [
      { status: 'Request Received', days: 0 },
      { status: 'Routed to Department', days: 0.5 },
      { status: 'Assigned to Staff', days: 1 },
      { status: 'Assessment Complete', days: 2 },
      { status: 'Work Scheduled', days: 3 },
      { status: 'Work in Progress', days: 5 },
      { status: 'Work Complete', days: daysOpen - 1 },
      { status: 'Verification', days: daysOpen - 0.5 }
    ];

    const numUpdates = status === 'Closed' ? statusUpdates.length : Math.min(Math.floor(Math.random() * 5 + 2), statusUpdates.length - 2);

    for (let j = 0; j < numUpdates; j++) {
      const update = statusUpdates[j];
      tracking.push({
        service_request_number: request.service_request_number,
        status: update.status,
        updated_at: new Date(createdDate.getTime() + update.days * 24 * 60 * 60 * 1000).toISOString(),
        department: department
      });
    }
  }

  return { requests, tracking };
};

